document.addEventListener("DOMContentLoaded",function(){let e=document.querySelector(".hamburger"),t=document.querySelector("nav"),c=document.querySelectorAll("nav a");e.addEventListener("click",function(){t.classList.toggle("active"),e.classList.toggle("active"),document.body.classList.toggle("menu-active")}),c.forEach(c=>{c.addEventListener("click",function(){t.classList.remove("active"),e.classList.remove("active"),document.body.classList.remove("menu-active")})}),document.addEventListener("click",function(c){t.contains(c.target)||e.contains(c.target)||(t.classList.remove("active"),e.classList.remove("active"),document.body.classList.remove("menu-active"))})});